CREATE VIEW courses.sections_full AS
 SELECT b.id AS book_id,
    b.title AS book_title,
    c.idx AS chapter_idx,
    c.title AS chapter_title,
    sc.idx AS subchapter_idx,
    sc.title AS subchapter_title,
    s.idx AS section_idx,
    s.title AS section_title,
    s.content
   FROM (((courses.books b
     JOIN courses.chapters c ON ((c.book_id = b.id)))
     JOIN courses.subchapters sc ON ((sc.chapter_id = c.id)))
     JOIN courses.sections s ON ((s.subchapter_id = sc.id)))
  ORDER BY b.id, c.idx, sc.idx, s.idx;


